echo -e Weather of \\033[32m$(curl -s http://ip-api.com/json | jq -r .city)\\033[m:
printf "  Temperature: \\033[33m$(curl -s "https://api.open-meteo.com/v1/forecast?latitude=$(curl -s http://ip-api.com/json | jq -r .lat)&longitude=$(curl -s http://ip-api.com/json | jq -r .lon)&current_weather=true" | jq -r .current_weather.temperature) \033[34m$(curl -s "https://api.open-meteo.com/v1/forecast?latitude=$(curl -s http://ip-api.com/json | jq -r .lat)&longitude=$(curl -s http://ip-api.com/json | jq -r .lon)&current_weather=true" | jq -r .current_weather_units.temperature)\\033[m\\n"
printf "  Wind velocity: \\033[33m$(curl -s "https://api.open-meteo.com/v1/forecast?latitude=$(curl -s http://ip-api.com/json | jq -r .lat)&longitude=$(curl -s http://ip-api.com/json | jq -r .lon)&current_weather=true" | jq -r .current_weather.windspeed) \033[34m$(curl -s "https://api.open-meteo.com/v1/forecast?latitude=$(curl -s http://ip-api.com/json | jq -r .lat)&longitude=$(curl -s http://ip-api.com/json | jq -r .lon)&current_weather=true" | jq -r .current_weather_units.windspeed)\\033[m\\n"
